import React, { useEffect } from 'react';
import queryString from 'query-string';

import './Chat.css'

const Chat = ({location}) => {
  const [name, setName] = useState('');
  const [room, setRoom] = useState('');

  useEffect(() => {
    const { name, room} = queryString.parse(location.search);

    setName(name);
    setRoom(room);
  }, []);

  return (
    <div>
        <h1>Hello Chat</h1>
    </div>
  )
}

export default Chat;
